mapper.update:
UPDATE board
   SET bd_hit = bd_hit + 1
 WHERE bd_no = #{bd_no}

mapper.select:
SELECT * FROM ( SELECT b.bd_no, b.bd_title, b.bd_content, b.bd_writer, b.bd_hit, b.bd_date, b.bd_imgpath, b.bd_modname, NVL(l.like_count, 0) AS bd_like, ROW_NUMBER() OVER (ORDER BY b.bd_date DESC) AS rn FROM board b LEFT JOIN ( SELECT bd_no, COUNT(*) AS like_count FROM likes GROUP BY bd_no ) l ON b.bd_no = l.bd_no WHERE b.bd_state = 'Y'
AND b.bd_cate = #{category}
AND b.bd_title LIKE '%' || #{keyword} || '%'
AND b.bd_content LIKE '%' || #{keyword} || '%'
AND b.bd_writer LIKE '%' || #{keyword} || '%'
) WHERE rn BETWEEN #{startRow} + 1 AND #{endRow} ORDER BY rn
