mapper.select:
SELECT *
	FROM (
		SELECT pr.*, ROWNUM AS rnum
		FROM (
			SELECT *
			FROM member
			ORDER BY mb_no DESC
	) pr
	WHERE ROWNUM <= #{param1}
)
WHERE rnum >= #{param2}

SELECT *
	FROM (
		SELECT pr.*, ROWNUM AS rnum
		FROM (
			SELECT *
			FROM member
			WHERE (mb_nickname LIKE #{param3}
			OR mb_id LIKE #{param3}
			OR mb_phone LIKE #{param3})
			ORDER BY mb_no DESC
	) pr
	WHERE ROWNUM <= #{param1}
)
WHERE rnum >= #{param2}

SELECT *
	FROM (
		SELECT pr.*, ROWNUM AS rnum
		FROM (
			SELECT *
			FROM member
			WHERE (mb_nickname LIKE #{param3})
			ORDER BY mb_no DESC
	) pr
	WHERE ROWNUM <= #{param1}
)
WHERE rnum >= #{param2}

SELECT *
	FROM (
		SELECT pr.*, ROWNUM AS rnum
		FROM (
			SELECT *
			FROM member
			WHERE (mb_id LIKE #{param3})
			ORDER BY mb_no DESC
	) pr
	WHERE ROWNUM <= #{param1}
)
WHERE rnum >= #{param2}

SELECT *
	FROM (
		SELECT pr.*, ROWNUM AS rnum
		FROM (
			SELECT *
			FROM member
			WHERE (mb_phone LIKE #{param3})
			ORDER BY mb_no DESC
	) pr
	WHERE ROWNUM <= #{param1}
)
WHERE rnum >= #{param2}

SELECT *
	FROM (
		SELECT pr.*, ROWNUM AS rnum
		FROM (
			SELECT *
			FROM member
			WHERE (mb_state = #{param3})
			ORDER BY mb_no DESC
	) pr
	WHERE ROWNUM <= #{param1}
)
WHERE rnum >= #{param2}
